import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { authApi, getCurrentUser, setCurrentUser } from "@/lib/auth";

export default function ProfileSection() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const currentUser = getCurrentUser();
  const [formData, setFormData] = useState({
    name: currentUser?.name || "",
    email: currentUser?.email || "",
    phone: currentUser?.phone || "",
    birthDate: currentUser?.birthDate || "",
    currentWeight: currentUser?.currentWeight || "",
    targetWeight: currentUser?.targetWeight || "",
    primaryGoal: currentUser?.primaryGoal || "Ganho de Massa",
  });

  const updateUserMutation = useMutation({
    mutationFn: (updates: any) => authApi.updateUser(currentUser!.id, updates),
    onSuccess: (updatedUser) => {
      setCurrentUser(updatedUser);
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Perfil atualizado!",
        description: "Suas informações foram salvas com sucesso.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao atualizar",
        description: error.message || "Não foi possível salvar as alterações",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateUserMutation.mutate({
      ...formData,
      currentWeight: formData.currentWeight ? parseInt(formData.currentWeight.toString()) : null,
      targetWeight: formData.targetWeight ? parseInt(formData.targetWeight.toString()) : null,
    });
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="max-w-4xl space-y-6">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Profile Photo and Basic Info */}
        <Card data-testid="card-profile-photo">
          <CardContent className="p-6">
            <div className="text-center">
              <img
                src="https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=400"
                alt="Foto do perfil"
                className="w-24 h-24 rounded-full mx-auto mb-4 object-cover"
                data-testid="img-profile-photo"
              />
              <h3 className="text-xl font-semibold text-card-foreground" data-testid="text-profile-name">
                {currentUser?.name}
              </h3>
              <p className="text-muted-foreground" data-testid="text-member-since">
                Membro desde {currentUser?.memberSince}
              </p>
              <Button variant="link" className="mt-3 text-sm" data-testid="button-change-photo">
                Alterar Foto
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Personal Information */}
        <Card className="lg:col-span-2" data-testid="card-personal-info">
          <CardHeader>
            <CardTitle data-testid="text-personal-info-title">Informações Pessoais</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4" data-testid="form-profile-update">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Nome Completo</Label>
                  <Input
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    data-testid="input-name"
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    data-testid="input-email"
                  />
                </div>
                <div>
                  <Label htmlFor="phone">Telefone</Label>
                  <Input
                    id="phone"
                    name="phone"
                    type="tel"
                    placeholder="(11) 99999-9999"
                    value={formData.phone}
                    onChange={handleInputChange}
                    data-testid="input-phone"
                  />
                </div>
                <div>
                  <Label htmlFor="birthDate">Data de Nascimento</Label>
                  <Input
                    id="birthDate"
                    name="birthDate"
                    type="date"
                    value={formData.birthDate}
                    onChange={handleInputChange}
                    data-testid="input-birth-date"
                  />
                </div>
              </div>
              <Button
                type="submit"
                disabled={updateUserMutation.isPending}
                data-testid="button-save-profile"
              >
                {updateUserMutation.isPending ? "Salvando..." : "Salvar Alterações"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>

      {/* Fitness Goals */}
      <Card data-testid="card-fitness-goals">
        <CardHeader>
          <CardTitle data-testid="text-fitness-goals-title">Objetivos Fitness</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label htmlFor="primaryGoal">Objetivo Principal</Label>
              <Select
                value={formData.primaryGoal}
                onValueChange={(value) => setFormData({ ...formData, primaryGoal: value })}
              >
                <SelectTrigger data-testid="select-primary-goal">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Perda de Peso">Perda de Peso</SelectItem>
                  <SelectItem value="Ganho de Massa">Ganho de Massa</SelectItem>
                  <SelectItem value="Condicionamento">Condicionamento</SelectItem>
                  <SelectItem value="Reabilitação">Reabilitação</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="currentWeight">Peso Atual (kg)</Label>
              <Input
                id="currentWeight"
                name="currentWeight"
                type="number"
                placeholder="75"
                value={formData.currentWeight}
                onChange={handleInputChange}
                data-testid="input-current-weight"
              />
            </div>
            <div>
              <Label htmlFor="targetWeight">Peso Meta (kg)</Label>
              <Input
                id="targetWeight"
                name="targetWeight"
                type="number"
                placeholder="80"
                value={formData.targetWeight}
                onChange={handleInputChange}
                data-testid="input-target-weight"
              />
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ui/menubar.tsx.content:
"use client"

import * as React from "react"
import * as MenubarPrimitive from "@radix-ui/react-menubar"
import { Check, ChevronRight, Circle } from "lucide-react"

import { cn } from "@/lib/utils"

function MenubarMenu({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Menu>) {
  return <MenubarPrimitive.Menu {...props} />
}

function MenubarGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Group>) {
  return <MenubarPrimitive.Group {...props} />
}

function MenubarPortal({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Portal>) {
  return <MenubarPrimitive.Portal {...props} />
}

function MenubarRadioGroup({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.RadioGroup>) {
  return <MenubarPrimitive.RadioGroup {...props} />
}

function MenubarSub({
  ...props
}: React.ComponentProps<typeof MenubarPrimitive.Sub>) {
  return <MenubarPrimitive.Sub data-slot="menubar-sub" {...props} />
}

const Menubar = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Root>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Root
    ref={ref}
    className={cn(
      "flex h-10 items-center space-x-1 rounded-md border bg-background p-1",
      className
    )}
    {...props}
  />
))
Menubar.displayName = MenubarPrimitive.Root.displayName

const MenubarTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Trigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Trigger>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Trigger
    ref={ref}
    className={cn(
      "flex cursor-default select-none items-center rounded-sm px-3 py-1.5 text-sm font-medium outline-none focus:bg-accent focus:text-accent-foreground data-[state=open]:bg-accent data-[state=open]:text-accent-foreground",
      className
    )}
    {...props}
  />
))
MenubarTrigger.displayName = MenubarPrimitive.Trigger.displayName

const MenubarSubTrigger = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.SubTrigger>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.SubTrigger> & {
    inset?: boolean
  }
>(({ className, inset, children, ...props }, ref) => (
  <MenubarPrimitive.SubTrigger
    ref={ref}
    className={cn(
      "flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[state=open]:bg-accent data-[state=open]:text-accent-foreground",
      inset && "pl-8",
      className
    )}
    {...props}
  >
    {children}
    <ChevronRight className="ml-auto h-4 w-4" />
  </MenubarPrimitive.SubTrigger>
))
MenubarSubTrigger.displayName = MenubarPrimitive.SubTrigger.displayName

const MenubarSubContent = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.SubContent>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.SubContent>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.SubContent
    ref={ref}
    className={cn(
      "z-50 min-w-[8rem] overflow-hidden rounded-md border bg-popover p-1 text-popover-foreground data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2 origin-[--radix-menubar-content-transform-origin]",
      className
    )}
    {...props}
  />
))
MenubarSubContent.displayName = MenubarPrimitive.SubContent.displayName

const MenubarContent = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Content>
>(
  (
    { className, align = "start", alignOffset = -4, sideOffset = 8, ...props },
    ref
  ) => (
    <MenubarPrimitive.Portal>
      <MenubarPrimitive.Content
        ref={ref}
        align={align}
        alignOffset={alignOffset}
        sideOffset={sideOffset}
        className={cn(
          "z-50 min-w-[12rem] overflow-hidden rounded-md border bg-popover p-1 text-popover-foreground shadow-md data-[state=open]:animate-in data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2 origin-[--radix-menubar-content-transform-origin]",
          className
        )}
        {...props}
      />
    </MenubarPrimitive.Portal>
  )
)
MenubarContent.displayName = MenubarPrimitive.Content.displayName

const MenubarItem = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Item> & {
    inset?: boolean
  }
>(({ className, inset, ...props }, ref) => (
  <MenubarPrimitive.Item
    ref={ref}
    className={cn(
      "relative flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
      inset && "pl-8",
      className
    )}
    {...props}
  />
))
MenubarItem.displayName = MenubarPrimitive.Item.displayName

const MenubarCheckboxItem = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.CheckboxItem>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.CheckboxItem>
>(({ className, children, checked, ...props }, ref) => (
  <MenubarPrimitive.CheckboxItem
    ref={ref}
    className={cn(
      "relative flex cursor-default select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
      className
    )}
    checked={checked}
    {...props}
  >
    <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
      <MenubarPrimitive.ItemIndicator>
        <Check className="h-4 w-4" />
      </MenubarPrimitive.ItemIndicator>
    </span>
    {children}
  </MenubarPrimitive.CheckboxItem>
))
MenubarCheckboxItem.displayName = MenubarPrimitive.CheckboxItem.displayName

const MenubarRadioItem = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.RadioItem>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.RadioItem>
>(({ className, children, ...props }, ref) => (
  <MenubarPrimitive.RadioItem
    ref={ref}
    className={cn(
      "relative flex cursor-default select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none focus:bg-accent focus:text-accent-foreground data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
      className
    )}
    {...props}
  >
    <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
      <MenubarPrimitive.ItemIndicator>
        <Circle className="h-2 w-2 fill-current" />
      </MenubarPrimitive.ItemIndicator>
    </span>
    {children}
  </MenubarPrimitive.RadioItem>
))
MenubarRadioItem.displayName = MenubarPrimitive.RadioItem.displayName

const MenubarLabel = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Label>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Label> & {
    inset?: boolean
  }
>(({ className, inset, ...props }, ref) => (
  <MenubarPrimitive.Label
    ref={ref}
    className={cn(
      "px-2 py-1.5 text-sm font-semibold",
      inset && "pl-8",
      className
    )}
    {...props}
  />
))
MenubarLabel.displayName = MenubarPrimitive.Label.displayName

const MenubarSeparator = React.forwardRef<
  React.ElementRef<typeof MenubarPrimitive.Separator>,
  React.ComponentPropsWithoutRef<typeof MenubarPrimitive.Separator>
>(({ className, ...props }, ref) => (
  <MenubarPrimitive.Separator
    ref={ref}
    className={cn("-mx-1 my-1 h-px bg-muted", className)}
    {...props}
  />
))
MenubarSeparator.displayName = MenubarPrimitive.Separator.displayName

const MenubarShortcut = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) => {
  return (
    <span
      className={cn(
        "ml-auto text-xs tracking-widest text-muted-foreground",
        className
      )}
      {...props}
    />
  )
}
MenubarShortcut.displayname = "MenubarShortcut"

export {
  Menubar,
  MenubarMenu,
  MenubarTrigger,
  MenubarContent,
  MenubarItem,
  MenubarSeparator,
  MenubarLabel,
  MenubarCheckboxItem,
  MenubarRadioGroup,
  MenubarRadioItem,
  MenubarPortal,
  MenubarSubContent,
  MenubarSubTrigger,
  MenubarGroup,
  MenubarSub,
  MenubarShortcut,
}

client/src/components/workouts-section.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { getCurrentUser } from "@/lib/auth";
import { apiRequest } from "@/lib/queryClient";
import { Plus, Flame, Clock, Trophy, Eye, Dumbbell, Play, Activity } from "lucide-react";

export default function WorkoutsSection() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const currentUser = getCurrentUser();
  const [newWorkoutOpen, setNewWorkoutOpen] = useState(false);
  const [workoutForm, setWorkoutForm] = useState({
    name: "",
    duration: "",
    calories: "",
    exercises: "",
  });

  const { data: workouts, isLoading } = useQuery({
    queryKey: ["/api/users", currentUser?.id, "workouts"],
    enabled: !!currentUser,
  });

  const createWorkoutMutation = useMutation({
    mutationFn: (workoutData: any) => apiRequest("POST", "/api/workouts", workoutData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users", currentUser?.id, "workouts"] });
      setNewWorkoutOpen(false);
      setWorkoutForm({ name: "", duration: "", calories: "", exercises: "" });
      toast({
        title: "Treino criado!",
        description: "Seu treino foi salvo com sucesso.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao criar treino",
        description: error.message || "Não foi possível salvar o treino",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createWorkoutMutation.mutate({
      userId: currentUser?.id,
      name: workoutForm.name,
      date: new Date().toISOString().split('T')[0],
      duration: parseInt(workoutForm.duration),
      calories: workoutForm.calories ? parseInt(workoutForm.calories) : null,
      exercises: workoutForm.exercises.split('\n').filter(ex => ex.trim()),
    });
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setWorkoutForm({
      ...workoutForm,
      [e.target.name]: e.target.value,
    });
  };

  // Calculate stats
  const totalCalories = (workouts as any[])?.reduce((sum: number, workout: any) => sum + (workout.calories || 0), 0) || 0;
  const totalTime = (workouts as any[])?.reduce((sum: number, workout: any) => sum + workout.duration, 0) || 0;
  const totalHours = Math.floor(totalTime / 60);
  const totalMinutes = totalTime % 60;

  // Calculate streak (simplified - consecutive days with workouts)
  const streak = 7; // Placeholder calculation

  const stats = [
    {
      title: "Calorias Queimadas",
      value: totalCalories.toLocaleString(),
      icon: Flame,
      color: "text-primary",
      bgColor: "bg-primary/10",
    },
    {
      title: "Tempo Total",
      value: `${totalHours}h ${totalMinutes}m`,
      icon: Clock,
      color: "text-green-500",
      bgColor: "bg-green-500/10",
    },
    {
      title: "Sequência de Dias",
      value: streak,
      icon: Trophy,
      color: "text-orange-500",
      bgColor: "bg-orange-500/10",
    },
  ];

  if (isLoading) {
    return (
      <div className="space-y-8">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded mb-6"></div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-32 bg-muted rounded"></div>
            ))}
          </div>
          <div className="h-64 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <h3 className="text-xl font-semibold text-card-foreground" data-testid="text-workouts-title">
          Meus Treinos
        </h3>
        <Dialog open={newWorkoutOpen} onOpenChange={setNewWorkoutOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-new-workout">
              <Plus className="mr-2 h-4 w-4" />
              Novo Treino
            </Button>
          </DialogTrigger>
          <DialogContent data-testid="dialog-new-workout">
            <DialogHeader>
              <DialogTitle>Registrar Novo Treino</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="name">Nome do Treino</Label>
                <Input
                  id="name"
                  name="name"
                  placeholder="Ex: Treino de Peito e Tríceps"
                  value={workoutForm.name}
                  onChange={handleInputChange}
                  required
                  data-testid="input-workout-name"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="duration">Duração (min)</Label>
                  <Input
                    id="duration"
                    name="duration"
                    type="number"
                    placeholder="45"
                    value={workoutForm.duration}
                    onChange={handleInputChange}
                    required
                    data-testid="input-workout-duration"
                  />
                </div>
                <div>
                  <Label htmlFor="calories">Calorias (opcional)</Label>
                  <Input
                    id="calories"
                    name="calories"
                    type="number"
                    placeholder="320"
                    value={workoutForm.calories}
                    onChange={handleInputChange}
                    data-testid="input-workout-calories"
                  />
                </div>
              </div>
              <div>
                <Label htmlFor="exercises">Exercícios (um por linha)</Label>
                <Textarea
                  id="exercises"
                  name="exercises"
                  placeholder="Supino reto 3x10&#10;Supino inclinado 3x10&#10;Tríceps pulley 3x12"
                  value={workoutForm.exercises}
                  onChange={handleInputChange}
                  rows={4}
                  data-testid="textarea-workout-exercises"
                />
              </div>
              <div className="flex gap-2">
                <Button
                  type="button"
                  variant="secondary"
                  onClick={() => setNewWorkoutOpen(false)}
                  data-testid="button-cancel-workout"
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  disabled={createWorkoutMutation.isPending}
                  data-testid="button-save-workout"
                >
                  {createWorkoutMutation.isPending ? "Salvando..." : "Salvar"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Workout Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index} data-testid={`stat-workout-${index}`}>
              <CardContent className="p-6">
                <div className="text-center">
                  <div className={`w-16 h-16 ${stat.bgColor} rounded-full flex items-center justify-center mx-auto mb-3`}>
                    <Icon className={`${stat.color} h-6 w-6`} />
                  </div>
                  <p className="text-2xl font-bold text-card-foreground" data-testid={`stat-workout-value-${index}`}>
                    {stat.value}
                  </p>
                  <p className="text-sm text-muted-foreground" data-testid={`stat-workout-label-${index}`}>
                    {stat.title}
                  </p>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Recent Workouts */}
      <Card data-testid="card-recent-workouts">
        <CardHeader>
          <CardTitle data-testid="text-recent-workouts-title">Treinos Recentes</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {(workouts as any[])?.length > 0 ? (
              (workouts as any[]).slice(0, 5).map((workout: any, index: number) => {
                const workoutIcons = [Dumbbell, Play, Activity];
                const colors = ["text-primary", "text-green-500", "text-purple-500"];
                const bgColors = ["bg-primary/10", "bg-green-500/10", "bg-purple-500/10"];

                const Icon = workoutIcons[index % workoutIcons.length];
                const color = colors[index % colors.length];
                const bgColor = bgColors[index % bgColors.length];

                return (
                  <div
                    key={workout.id}
                    className="flex items-center justify-between p-4 bg-muted rounded-lg"
                    data-testid={`workout-${index}`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className={`w-10 h-10 ${bgColor} rounded-lg flex items-center justify-center`}>
                        <Icon className={`${color} h-5 w-5`} />
                      </div>
                      <div>
                        <p className="font-medium text-card-foreground" data-testid={`workout-name-${index}`}>
                          {workout.name}
                        </p>
                        <p className="text-sm text-muted-foreground" data-testid={`workout-details-${index}`}>
                          {workout.date} • {workout.duration} min • {workout.calories || 0} cal
                        </p>
                      </div>
                    </div>
                    <Button variant="ghost" size="icon" data-testid={`button-view-workout-${index}`}>
                      <Eye className="h-4 w-4" />
                    </Button>
                  </div>
                );
              })
            ) : (
              <div className="text-center py-8">
                <Dumbbell className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                <p className="text-muted-foreground" data-testid="text-no-workouts">
                  Nenhum treino registrado ainda
                </p>
                <p className="text-sm text-muted-foreground mt-2">
                  Comece registrando seu primeiro treino!
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

