import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { getCurrentUser } from "@/lib/auth";
import { CheckCircle, Calendar, Trophy } from "lucide-react";

export default function RecentActivity() {
  const currentUser = getCurrentUser();

  const { data: workouts } = useQuery({
    queryKey: ["/api/users", currentUser?.id, "workouts"],
    enabled: !!currentUser,
  });

  const { data: checkins } = useQuery({
    queryKey: ["/api/users", currentUser?.id, "checkins"],
    enabled: !!currentUser,
  });

  // Create activity feed from workouts and checkins
  const activities = [];

  if (workouts && (workouts as any[]).length > 0) {
    const latestWorkout = (workouts as any[])[0];
    activities.push({
      type: "workout",
      title: latestWorkout.name,
      time: "Hoje às 14:30",
      icon: CheckCircle,
      color: "text-green-500",
      bgColor: "bg-green-500/10",
    });
  }

  activities.push({
    type: "booking",
    title: "Aula de Yoga agendada",
    time: "Amanhã às 19:00",
    icon: Calendar,
    color: "text-primary",
    bgColor: "bg-primary/10",
  });

  if (workouts && (workouts as any[]).length >= 10) {
    activities.push({
      type: "achievement",
      title: "Meta de 10 treinos atingida!",
      time: "Ontem",
      icon: Trophy,
      color: "text-yellow-500",
      bgColor: "bg-yellow-500/10",
    });
  }

  return (
    <Card data-testid="card-recent-activity">
      <CardHeader>
        <CardTitle data-testid="text-activity-title">Atividade Recente</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {activities.length > 0 ? (
            activities.map((activity, index) => {
              const Icon = activity.icon;
              return (
                <div key={index} className="flex items-center space-x-3" data-testid={`activity-${index}`}>
                  <div className={`w-8 h-8 ${activity.bgColor} rounded-full flex items-center justify-center`}>
                    <Icon className={`${activity.color} h-4 w-4`} />
                  </div>
                  <div>
                    <p className="text-sm font-medium text-card-foreground" data-testid={`activity-title-${index}`}>
                      {activity.title}
                    </p>
                    <p className="text-xs text-muted-foreground" data-testid={`activity-time-${index}`}>
                      {activity.time}
                    </p>
                  </div>
                </div>
              );
            })
          ) : (
            <p className="text-muted-foreground text-center py-4" data-testid="text-no-activity">
              Nenhuma atividade recente
            </p>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/sidebar.tsx.content:
import { Button } from "@/components/ui/button";
import { X, LayoutDashboard, User, Calendar, Dumbbell, CreditCard, Wrench, Clock, LogOut, Shield } from "lucide-react";
import { getCurrentUser } from "@/lib/auth";

interface SidebarProps {
  activeSection: string;
  onSectionChange: (section: string) => void;
  onLogout: () => void;
  isOpen: boolean;
  onClose: () => void;
}

export default function Sidebar({ activeSection, onSectionChange, onLogout, isOpen, onClose }: SidebarProps) {
  const currentUser = getCurrentUser();
  const isAdmin = (currentUser as any)?.isAdmin;

  const menuItems = [
    { id: "dashboard", label: "Dashboard", icon: LayoutDashboard },
    { id: "profile", label: "Meu Perfil", icon: User },
    { id: "classes", label: "Aulas", icon: Calendar },
    { id: "workouts", label: "Treinos", icon: Dumbbell },
    { id: "membership", label: "Planos", icon: CreditCard },
    { id: "equipment", label: "Equipamentos", icon: Wrench },
    { id: "checkin", label: "Check-in", icon: Clock },
    ...(isAdmin ? [{ id: "admin", label: "Logs Admin", icon: Shield }] : []),
  ];

  return (
    <div
      className={`fixed left-0 top-0 h-full w-64 bg-primary text-primary-foreground transform transition-transform duration-300 ease-in-out z-50 lg:translate-x-0 ${
        isOpen ? "translate-x-0" : "-translate-x-full"
      }`}
      data-testid="sidebar"
    >
      <div className="flex items-center justify-between p-4 border-b border-primary-foreground/20">
        <h1 className="text-xl font-bold" data-testid="text-sidebar-title">SportFitness</h1>
        <Button
          variant="ghost"
          size="icon"
          className="lg:hidden text-primary-foreground hover:bg-primary-foreground/10"
          onClick={onClose}
          data-testid="button-close-sidebar"
        >
          <X className="h-4 w-4" />
        </Button>
      </div>

      <nav className="mt-6">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeSection === item.id;

          return (
            <button
              key={item.id}
              onClick={() => {
                onSectionChange(item.id);
                onClose();
              }}
              className={`w-full flex items-center px-4 py-3 text-left text-primary-foreground/80 hover:bg-primary-foreground/10 hover:text-primary-foreground transition-colors ${
                isActive ? "border-r-2 border-primary-foreground text-primary-foreground" : ""
              }`}
              data-testid={`nav-${item.id}`}
            >
              <Icon className="mr-3 h-5 w-5" />
              {item.label}
            </button>
          );
        })}
      </nav>

      <div className="absolute bottom-4 left-4 right-4">
        <Button
          onClick={onLogout}
          className="w-full bg-destructive text-destructive-foreground hover:bg-destructive/90"
          data-testid="button-logout"
        >
          <LogOut className="mr-2 h-4 w-4" />
          Sair
        </Button>
      </div>
    </div>
  );
}

